Ext.define('Google.view.InformationDesk', {
    extend: 'Ext.dataview.List',
    alias: 'widget.InformationDesk',

    config: {
        store: 'InformationStore',
        itemTpl: [
            '<div class="blue-text-norm-small">{title}</div>'
        ],
        listeners: [
            {
                fn: 'onListInitialize',
                event: 'initialize'
            },
            {
                fn: 'onListItemTap',
                event: 'itemtap'
            }
        ]
    },

    onListInitialize: function(component, options) {
        // Load in the stores


        //Load the Speakers Store in
        Ext.getStore('InformationStore').load();
    },

    onListItemTap: function(dataview, index, target, record, e, options) {
        this.up('NavView').push({
            xtype: 'InfoPanel',
            data: record.data,
            title: 'Details'
        });
    }

});